}

/*Title Bar Height*/
.withFrame-haYltI {
  background: var(--background-tertiary);
  padding: 8px 16px;
  height: 24px;
  margin-top: unset;
  position: relative;
  left: 90px;
  width: calc(100% - 122px);
  z-index: 1;
  pointer-events: none;
}

/*Discord text on left*/
.withFrame-haYltI .wordmark-2iDDfm {
  top: unset;
  left: 16px;
  color: transparent;
}

/*Windows Buttons*/
.withFrame-haYltI .winButton-iRh8-Z {
  color: var(--textcolor);
  top: -8px;
  right: -16px;
  height: 40px;
  width: 56px;
}

.theme-light { /* I don't support light theme it's just for the "Create a server" popup */
    --background-tertiary: #101320;
    --background-secondary: #1e2233;
    --background-secondary-alt: #191f2e;
